class Solution {

    static final int INF = Integer.MAX_VALUE;

    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        int result = 0;
        for(int i = 0; i < n; ++i) {
            result = Math.max(result, helper(nums[i], i, queries));
        }
        return result == INF ? -1 : result;
    }

    static int helper(int target, int i, int[][] queries) {
        if (target == 0) {
            return 0;
        }
        var dp = new boolean[target+1];
        dp[0] = true;
        int result = 0;
        for(var query : queries) {
            ++result;
            if (i >= query[0] && i <= query[1]) {
                int v = query[2];
                for(int j = target; j - v >= 0; --j) {
                    dp[j] = dp[j] || dp[j - v];
                }
            }
            if (dp[target]) {
                return result;
            }
        }
        return INF;
    }
}

