class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        if not any(nums):
            return 0
        reachable = [{n} for n in nums]
        done = [n == 0 for n in nums]
        count = sum(done)
        for k,(l,r,v) in enumerate(queries,1):
            for i in range(l,r+1):
                if done[i]:
                    continue
                if v in reachable[i]:
                    done[i] = True
                    count += 1
                    if count == len(nums):
                        return k
                    continue
                new = set()
                for x in reachable[i]:
                    if x > v:
                        new.add(x-v)
                reachable[i] |= new
        return -1

