class Solution {
public:
    vector<vector<int>> dp;
    int dfs(int i, int val, int j, vector<vector<int>>& qs) {
        if (val == 0)
            return i;
        if (val < 0 || i == qs.size())
            return INT_MAX;
        if (dp[i][val] == -1) {
            dp[i][val] = dfs(i + 1, val, j, qs);
            if (qs[i][0] <= j && j <= qs[i][1])
                dp[i][val] = min(dp[i][val], dfs(i + 1, val - qs[i][2], j, qs));
        }
        return dp[i][val];
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int res = 0;
        for (int i = 0; res != INT_MAX && i < nums.size(); ++i) {
            dp = vector<vector<int>> (queries.size(), vector<int>(1001, -1));
            res = max(res, dfs(0, nums[i], i, queries));
        }
        return res == INT_MAX ? -1 : res;
    }
};

