var minZeroArray = function(nums, queries) {
    const n = nums.length;
    const achievable_sums = Array.from({length: n}, () => new Set([0]));
    const achieved = Array(n).fill(false);
    let achieved_count = 0;

    // Check initial state
    for (let j = 0; j < n; j++) {
        if (nums[j] === 0) {
            achieved[j] = true;
            achieved_count++;
        }
    }
    if (achieved_count === n) return 0;

    // Process each query
    for (let k = 0; k < queries.length; k++) {
        const [left, right, value] = queries[k];
        for (let j = left; j <= right; j++) {
            if (achieved[j]) continue; // Skip if already achieved
            const current_sums = achievable_sums[j];
            const new_sums = new Set();
            for (const sum of current_sums) {
                const next_sum = sum + value;
                if (next_sum <= nums[j]) {
                    new_sums.add(next_sum);
                    if (next_sum === nums[j]) {
                        achieved[j] = true;
                        achieved_count++;
                    }
                }
            }
            new_sums.forEach(sum => current_sums.add(sum));
        }
        if (achieved_count === n) return k + 1;
    }

    return -1;
};
