            for (int j = queries[i][0]; j <= queries[i][1]; ++j) {
                count[j][queries[i][2]]++;
            }
        }
        for (int i = 0; i < nums.size(); ++i) {
            vector<int> dp(nums[i] + 1, 0);
            dp[0] = 1;

            for (int j = 1; j <= 10; ++j) {
                int c = count[i][j];
                for (int k = nums[i]; k >= 0; --k) {
                    if (dp[k]) {
                        for (int l = 1; l <= c; l++) {
                            int newSum = k + j * l;
                            if (newSum > nums[i]) break;
                            dp[newSum] = true;
                        }
                    }
                }
            }
            if (dp[nums[i]] == false) return false;
        }
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size(), states = 1 << n;
        int left = 0, right = queries.size();
        while (left <= right) {
            int middle = (left + right) / 2;
            if (check(nums, queries, middle)) right = middle - 1;
            else left = middle + 1;
        }

        return left > m ? -1 : left;
    }
};

