class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        coverage = [[] for _ in range(n)]
        for i, (l, r, val) in enumerate(queries):
            for j in range(l, r + 1):
                coverage[j].append((i, val))
        max_i_list = []
        for j in range(n):
            if nums[j] == 0:
                max_i_list.append(-1)
                continue
            target = nums[j]
            queries_j = coverage[j]
            dp = {0: -1}
            for i, val in queries_j:
                new_dp = {}
                for s in dp:
                    prev_max = dp[s]
                    if s not in new_dp or prev_max < new_dp[s]:
                        new_dp[s] = prev_max
                    new_s = s + val
                    new_max = max(prev_max, i)
                    if new_s <= target:
                        if new_s not in new_dp or new_max < new_dp.get(new_s, inf):
                            new_dp[new_s] = new_max
                dp = new_dp

            if target in dp:
                max_i_list.append(dp[target])
            else:
                return -1
        max_i = max(max_i_list)
        # print(max_i_list)
        return max_i + 1

