class Solution {
    bool subsetSum(const vector<int>& arr, int target) {
        int n = arr.size();
        vector<vector<bool>> dp(n + 1, vector<bool>(target + 1, false));
        for (int i = 0; i <= n; i++) dp[i][0] = true;
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= target; j++) {
                if (arr[i - 1] > j) {
                    dp[i][j] = dp[i - 1][j];
                } else {
                    dp[i][j] = dp[i - 1][j] || dp[i - 1][j - arr[i - 1]];
                }
            }
        }
        return dp[n][target];
    }
    int minidx(int num, vector<int> idx, vector<vector<int>>& queries) {
        int nq = queries.size();
        int low = 0;
        int high = nq+1;
        while (low < high) {
            int mid = (high - low) / 2 + low;
            vector<int> arr;
            for (int i: idx) {
                if (i < mid) arr.push_back(queries[i][2]);
            }
            if (subsetSum(arr, num)) {
                high = mid;
            } else {
                low = mid + 1;
            }
        }
        return low;
    }

public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int nq = queries.size();
        unordered_map<int, vector<int>> m;
        for (int i = 0; i < n; i++) {
            m[i] = vector<int>{};
        }
        for (int j = 0; j < nq; j++) {
            vector<int>& q = queries[j];
            int l = q[0];
            int r = q[1];
            for (int i = l; i <= r; i++) {
                m[i].push_back(j);
            }
        }
        int ret = 0;
        for (int i = 0; i < n; i++) {
            ret = max(ret, minidx(nums[i], m[i], queries));
        }
        if (ret == nq+1) ret = -1;
        return ret;

    }
};

