from functools import cache
inf = float('inf')
class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        if sum(nums) == 0: return 0 
        n = len(nums)
        choices = [[] for i in range(n)]
        for i,(l,r,val) in enumerate(queries):
            for j in range(l,r+1):
                choices[j].append((i,val))

        @cache
        def DP(i,j,x):
            if j == len(choices[i]):
                return inf

            mn = DP(i,j+1,x)
            k,v = choices[i][j]
            if x > v:
                mn = min(mn, DP(i,j+1,x-v))
            elif x == v:
                mn = min(mn, k+1)
            return mn

        mx = 0
        # print("---"*n)
        for i in range(n):
            if nums[i] > 0:
                # print()
                mx = max(mx, DP(i,0,nums[i]))

        DP.cache_clear()
        return -1 if mx == inf else mx

