import java.util.ArrayList;
import java.util.Arrays;
import java.util.HashMap;
import java.util.HashSet;

class Solution {
    public static boolean valid(ArrayList<Integer> nums, int target) {
        boolean[] dp = new boolean[target + 1];
        dp[0] = true;

        for (int num : nums) {
            for (int j = target; j >= num; j--) {
                dp[j] = dp[j] || dp[j - num];
            }
        }
        return dp[target];
    }
    public int minZeroArray(int[] nums, int[][] queries) {
        HashMap<Integer, ArrayList<Integer>> map = new HashMap<>();
        for (int i = 0; i < nums.length; i++) {
            map.put(i, new ArrayList<>());
        }
        if (nums.length == 1) {
            if (nums[0] == 0) {
                return 0;
            }
        }
        boolean isAllZero = true;
        for (int i: nums) {
            if (i != 0) {
                isAllZero = false;
            }
        }
        if (isAllZero) {
            return 0;
        }
        boolean[] zArr = new boolean[nums.length];
        for (int i = 0 ; i< queries.length; i++) {
            int[] q = queries[i];
            int l = q[0];
            int r = q[1];
            int v = q[2];
            for (int j = l; j <= r; j++) {
                map.get(j).add(v);
            }
            boolean failed = false;
            for (int j = 0; j < nums.length; j++) {
                if (zArr[j]) {
                    continue;
                }
                failed = true;
                zArr[j] = valid(map.get(j), nums[j]);
            }
            if (!failed) {
                return i;
            }
        }
        boolean f1 = true;
        for (boolean g: zArr) {
            if (g == false) {
                f1 = false;
            }
        }
        if (f1) {
            return queries.length;
        }
        return -1;
    }
}

