        int n=coins.size();
        bool dp[n+1][num+1];
        for(int i=0;i<=n;i++){
            dp[i][0]=true;
        }
        for(int j=1;j<=num;j++){
            dp[n][j]=false;
        }
        for(int i=n-1;i>=0;i--){
            for(int j=1;j<=num;j++){
                dp[i][j]=dp[i+1][j];
                if(j>=coins[i]){
                    dp[i][j]=dp[i][j]||dp[i+1][j-coins[i]];
                }
            }
        }
        return dp[0][num];
    }    

