        int n=nums.size();
        vector<int> vals[n];
        for(int q=0;q<ql;q++){
            for(int i=queries[q][0];i<=queries[q][1];i++){
                vals[i].push_back(queries[q][2]);
            }
        }
        bool ans=true;
        for(int i=0;i<n;i++){
            ans=ans&&subsetSum(nums[i],vals[i]);
        }
        return ans;
    }
