        int l=0,r=queries.size(),ans=-1;
        while(l<=r){
            int mid=l+(r-l)/2;
            if(check(nums,queries,mid)){
                ans=mid;
                r=mid-1;
            }
            else l=mid+1;
        }        
        return ans;
    }
};

