class Solution {
public:
    bool solve(int i, int sum, int target, vector<int>& choice)
    {
        int n = choice.size();
        if (sum == target)
            return true;
        if (i == n)
            return false;
        if (sum > target)
            return false;
        if (dp[i][sum] != -1)
            return dp[i][sum];
        bool res = solve(i + 1, sum, target, choice) || solve(i + 1, sum + choice[i], target, choice);
        return dp[i][sum] = res;
    }

    bool check(int x, vector<int>& nums, vector<vector<int>>& queries)
    {
        int n = nums.size();
        int m = queries.size();
        vector<vector<int>> choice(n);
        for (int i = 0; i < x; i++)
        {
            auto& q = queries[i];
            for (int i = q[0]; i <= q[1]; i++)
                choice[i].push_back(q[2]);
        }
        int good = 0;
        for (int i = 0; i < n; i++)
        {
            memset(dp, -1, sizeof(dp));
            good += solve(0, 0, nums[i], choice[i]);
        }
        return good == n;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int m = queries.size();
        int left = 0;
        int right = m;
        while (left <= right)
        {
            int mid = left + (right - left) / 2;
            if (check(mid, nums, queries))
                right = mid - 1;
            else
                left = mid + 1;
        }
        if (right + 1 == m + 1)
            return -1;
        return right + 1;
    }
};

