class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        if all(x == 0 for x in nums):
            return 0

        n = len(nums)
        m = len(queries)
        element_queries = [[] for _ in range(n)]

        for orig_idx, (l, r, val) in enumerate(queries):
            for i in range(l, r + 1):
                element_queries[i].append((val, orig_idx))

        max_k = 0

        for i in range(n):
            if nums[i] == 0:
                continue

            target = nums[i]
            Q_i = element_queries[i]

            if not Q_i:
                return -1

            dp = {0}
            found = False
            current_k = -1

            for val, orig_idx in Q_i:
                new_s_possible = set()
                for s in list(dp):
                    new_s = s + val
                    if new_s == target:
                        current_k = orig_idx + 1
                        found = True
                        break
                    if new_s < target:
                        new_s_possible.add(new_s)
                if found:
                    break

                merged = dp.union(new_s_possible)
                if target in merged:
                    current_k = orig_idx + 1
                    found = True
                    break
                dp = merged

            if not found:
                return -1

            if current_k > max_k:
                max_k = current_k

        return max_k if max_k != 0 else -1

