class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        int ret = 0;

        for (int i = 0; i < n; ++i) {
            if (nums[i] == 0) {
                continue;
            }

            unordered_set<int> prev;    
            unordered_set<int> curr;
            prev.insert(nums[i]);
            for (int j = 0; j < m; ++j) {
                if (queries[j][0] > i || queries[j][1] < i) {
                    continue;
                }

                int val = queries[j][2];
                for (const auto p: prev) {
                    if (p >= val) {
                        curr.insert(p - val);
                    }
                }

                prev.insert(curr.begin(), curr.end());
                curr.clear();

                if (prev.find(0) != prev.end()) {
                    ret = max(ret, j + 1);
                    break;
                }
            }

            if (prev.find(0) == prev.end()) {
                return -1;
            }
        }

        return ret;
    }
};

