from typing import List, Set


class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        num_sets: List[Set[int]] = [set([num]) for num in (nums)]

        def update_num_set(i: int, v: int):
            s1 = num_sets[i]

            if 0 in s1:
                return

            s2 = set([num - v for num in s1 if num - v >= 0])

            num_sets[i] = s1 | s2

        def is_zero_array():
            for s in num_sets:
                if 0 not in s:
                    return False
            return True

        if is_zero_array():
            return 0

        for j, q in enumerate(queries):
            l, r, v = q

            for i in range(l, r + 1):
                update_num_set(i, v)

            if is_zero_array():
                return j + 1

        return -1

