class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        left = 0
        right = len(queries)
        n = len(nums)

        def can_be_zero(cur, target):
            mask = 1 << 0
            for num in cur:
                mask |= (mask << num)
                if (mask & (1 << target)) != 0:
                    return True
            return (mask & (1 << target)) != 0

        def check(k):
            for i in range(n):
                cur = []
                for j in range(k):
                    l, r, v = queries[j]
                    if l <= i <= r:
                        cur.append(v)
                if not can_be_zero(cur, nums[i]):
                    return False
            return True

        if not check(right):
            return -1
        while left < right:
            mid = (left + right) // 2
            if check(mid):
                right = mid
            else:
                left = mid + 1
        return left

