class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = sz(nums), Q = sz(queries);
        vector<bitset<1001>> v(n);
        for(int i = 0; i < n; ++i) v[i][nums[i]] = 1;
        function<bool()> check = [&](){
            bool work = 1;
            for(int i = 0; i < n; ++i) work &= v[i][0];
            return work;
        };
        for(int q = 0; q < Q; ++q){
            if(check()) return q;
            int l = queries[q][0], r = queries[q][1], val = queries[q][2];
            for(int i = l; i <= r; ++i){
                for(int j = 0; j <= nums[i]; ++j){
                    if(j - val < 0) continue;
                    v[i][j-val] = v[i][j-val] | v[i][j];
                }
            }
        }
        if(check()) return Q;
        else return -1;
    }
};

