class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        def helper(i, num):
            if num == 0:
                return 0
            curr = set([num])
            for j, (l, r, val) in enumerate(queries, 1):
                if l <= i <= r:
                    for t in list(curr):
                        if t == val:
                            return j
                        curr.add(t - val)
            return -1
        ans = 0
        for i, num in enumerate(nums):
            t = helper(i, num)
            # print(i, num, t)
            if t == -1:
                return -1
            ans = max(ans, t)
        return ans

