class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int ql = queries.length;
        int low=0,high=ql-1,mid;
        int ans=-1;
        if(check(-1,nums,queries)) {
            return 0;
        }
        while(low<=high) {
            mid=(low+high)/2;
            if(check(mid,nums,queries)) {
                ans=mid;
                high=mid-1;
            } else {
                low=mid+1;
            }
        }
        return ans==-1 ? -1 : ans+1;
    }
    private boolean check(int k,int[] nums, int[][] queries) {
        List<List<Integer>> lst = new ArrayList<>();
        int len = nums.length;
        for(int i=0;i<len;i++) {
            lst.add(new ArrayList<>());
        }
        //
        for(int idx=0;idx<=k;idx++) {
            for(int i=queries[idx][0];i<=queries[idx][1];i++) {
                lst.get(i).add(queries[idx][2]);
            }
        }
        //
        for(int i=0;i<len;i++) {
            if(!subSetSum(nums[i],lst.get(i))) {
                return false;
            }
        }
        return true;
    }
    private boolean subSetSum(int val,List<Integer> nums) {
        boolean dp[] = new boolean[val+1];
        dp[0]=true;
        for(int num : nums) {
            for(int j=val;j>=num;j--) {
                dp[j]|=dp[j-num];
            }
        }
        return dp[val];
    }
}

