class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int start = 0;
        int end = queries.length;
        int ans = -1;
        while(start<=end){
            int mid = start + (end-start)/2;
            if(poss(mid, nums, queries)){
                ans = mid;
                end = mid-1;
            }else{
                start = mid+1;
            }
        }

        return ans;
    }

    private boolean poss(int mid, int[] nums, int[][] queries){
        int n = nums.length;
        List<Integer>[] arr = new ArrayList[n];
        Arrays.setAll(arr, i->new ArrayList<>());
        for(int i = 0; i<mid; i++){
            for(int j = queries[i][0]; j<=queries[i][1]; j++){
                arr[j].add(queries[i][2]);
            }
        }
        for(int i = 0; i<n; i++){
            Boolean[][] dp = new Boolean[arr[i].size()][nums[i]+1];
            if(!poss(0, nums[i], arr[i], dp)){
                return false;
            }
        }

        return true;
    }

    private boolean poss(int idx, int val, List<Integer> al, Boolean[][] dp){
        if(val < 0) return false;
        if(idx == al.size()){
            return val == 0;
        }else{
            if(dp[idx][val]!=null) return dp[idx][val];

            return dp[idx][val] = poss(idx+1, val-al.get(idx), al, dp) || poss(idx+1, val, al, dp);
        }
    }
}

