class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        if len(set(nums)) == 1 and nums[0] == 0:
            return 0

        values = [{0} for _ in range(len(nums))]
        for k in range(len(queries)):
            l, r, v0 = queries[k]
            covered = 0
            for pos in range(len(nums)):
                target = nums[pos]
                if target in values[pos]:
                    covered += 1
                    continue
                if l <= pos <= r:
                    set1 = values[pos].copy()
                    for v in values[pos]:
                        if v + v0 > target:
                            continue
                        else:
                            set1.add(v + v0)
                            if v + v0 == target:
                                covered += 1
                    values[pos] = set1

            if covered == len(nums):
                return k + 1

        return -1

