class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int done = 0;
        vector<vector<bool>> vec;
        for (const int& i : nums) {
            vec.push_back(vector<bool>(i + 1, false));
            vec.back()[i] = true;
            if (i == 0) done++;
        }

        int k = 0;
        for (const vector<int>& q : queries) {
            if (done == nums.size()) return k;

            for (int i = q[0]; i <= q[1]; i++) {
                //for (const int j : vec[i]) cout << j << " ";
                //cout << endl;
                if (vec[i][0]) continue;
                for (int j = q[2]; j < vec[i].size(); j++) {
                    if (vec[i][j]) {
                        vec[i][j - q[2]] = true;
                        if (j == q[2]) {
                            done++;
                            continue;
                        }
                    }
                }
            }
            k++;
        }
        if (done == nums.size()) return k;
        return -1;
    }
};
