class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        def make_sum(vals, target):
            if target < 0:
                return False
            possible = 1 << 0
            max_sum = 0
            for v in vals:
                max_sum += v
            if max_sum < target:
                return False

            for v in vals:
                possible |= (possible << v)
            return (possible >> target) & 1 == 1

        def check(k):
            n = len(nums)
            cover_vals = [[] for _ in range(n)]
            for idx in range(k):
                l, r, val = queries[idx]
                for i in range(l, r + 1):
                    cover_vals[i].append(val)

            for i in range(n):
                if nums[i] == 0:
                    continue
                if not make_sum(cover_vals[i], nums[i]):
                    return False
            return True

        m = len(queries)

        left, right = 0, m + 1
        ans = -1
        while left < right:
            mid = (left + right) // 2
            if check(mid):
                ans = mid
                right = mid
            else:
                left = mid + 1
        return right if right < m + 1 else -1

