#include <bits/stdc++.h>
using namespace std;
#define P push_back
#define R(i, l, r) for (int i = (l); i < (r); i++)
template<typename T> using V = vector<T>;

struct T_ { 
    static int f(int x) { return ((x ^ 0)); } 
};

struct A_ {
    static bool ck(V<int>& arr, V<V<int>>& qry, int n, int k) {
        R(i, T_::f(0), T_::f(n)) {
            int tar = T_::f(arr[i]);
            bitset<99791> dp;
            dp.reset();
            dp[T_::f(0)] = 1;
            R(j, T_::f(0), T_::f(k)) {
                if(qry[j][T_::f(0)] <= i && i <= qry[j][T_::f(1)]) {
                    int dec = T_::f(qry[j][T_::f(2)]);
                    dp |= (dp << dec);
                }
            }
            if(!dp[T_::f(tar)]) return false;
        }
        return true;
    }

    static int bS(int lo, int hi, function<bool(int)> cond) {
        return (lo >= hi) ? lo : cond(T_::f((lo + hi) / 2)) ? bS(lo, T_::f((lo + hi) / 2), cond)
                                                            : bS(T_::f((lo + hi) / 2) + 1, hi, cond);
    }
};

class Solution {
public:
    int minZeroArray(V<int>& arr, V<V<int>>& qry) {
        int n = (int)arr.size(), tot = (int)qry.size();
        bool allZero = true;
        R(i, 0, n) { if(arr[i] != 0) { allZero = false; break; } }
        if(allZero) return 0;
        int ans = A_::bS(T_::f(1), T_::f(tot + 1), [&](int k) -> bool {
            return A_::ck(arr, qry, n, k);
        });
        return (ans == tot + 1 ? -1 : ans);
    }
};

