#include <vector>
#include <bitset>
#include <algorithm>
using namespace std;

class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        // Precompute for each index, the queries that cover it as (query_index, value)
        vector<vector<pair<int, int>>> covers(n);
        for (int j = 0; j < m; j++) {
            int l = queries[j][0], r = queries[j][1], v = queries[j][2];
            for (int i = l; i <= r; i++) {
                covers[i].push_back({j, v});
            }
        }
        // It is useful to sort each list by the query index
        for (int i = 0; i < n; i++) {
            sort(covers[i].begin(), covers[i].end());
        }

        // Given a prefix size k, check if for every index we can form exactly nums[i] using queries with index < k.
        auto feasible = [&](int k) -> bool {
            for (int i = 0; i < n; i++) {
                int target = nums[i];
                if (target == 0) continue; // Already satisfied

                // dp[j] will be true if a sum j is achievable using some subset of available query values.
                // Maximum target is at most 1000 so we use bitset<1001>.
                bitset<1001> dp;
                dp.reset();
                dp[0] = 1;
                // Process each query (in order) that covers index i and whose index is less than k.
                for (auto &pr : covers[i]) {
                    if (pr.first >= k) break;
                    int val = pr.second;
                    // Update dp: any sum that was reachable now can also reach sum+val.
                    dp |= (dp << val);
                    if (dp[target]) break;  // early exit if target is reachable
                }
                if (!dp[target])
                    return false;
            }
            return true;
        };

        // Binary search for the smallest k such that the prefix is feasible.
        int left = 0, right = m + 1;
        while (left < right) {
            int mid = left + (right - left) / 2;
            if (feasible(mid))
                right = mid;
            else
                left = mid + 1;
        }
        return (left <= m ? left : -1);
    }
};

