class Solution 
{
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) 
    {
        if(*max_element(nums.begin(), nums.end()) == 0)
        {
            return 0;
        }
        int n = nums.size(), m = queries.size();
        vector<vector<bool>> greed(n, vector<bool>(1001));
        for(int i = 0; i < n; i++)
        {
            greed[i][0] = true;
        }
        for(int i = 0; i < m; i++)
        {
            int l = queries[i][0];
            int r = queries[i][1];
            int val = queries[i][2];
            for(int j = l; j <= r; j++)
            {
                if(greed[j][nums[j]])
                {
                    continue;
                }
                for(int k = nums[j]; k - val >= 0; k--)
                {
                    greed[j][k] = greed[j][k] || greed[j][k - val];
                }
            }
            bool fin = true;;
            for(int j = 0; j < n && fin; j++)
            {
                if(!greed[j][nums[j]])
                {
                    fin = false;
                }
            }
            if(fin)
            {
                return i + 1;
            }
        }
        return -1;
    }
};

