class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int q = queries.size();
        int l = 0, r = q;
        while (l < r) {
            int m = (l + r) / 2;
            if (possible(m, nums, queries)) {
                r = m;
            } else {
                l = m+1;
            }
        }
        if (possible(l, nums, queries)) {
            return l;
        }
        return -1;
    }

    bool possible(int m, vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<vector<int>> coins(n);
        for (int i = 0; i < m; i++) {
            auto q = queries[i];
            for (int j = q[0]; j <= q[1]; j++) {
                coins[j].push_back(q[2]);
            }
        }
        for (int t = 0; t < n; t++) {
            vector<bool> dp(nums[t]+1, false);
            dp[0] = true;
            for (int c : coins[t]) {
                for (int i = nums[t]; i >= c; i--) {
                    if (dp[i-c]) dp[i] = true;
                }
            }
            if (!dp[nums[t]]) return false;
        }
        return true;
    }
};

