import java.util.HashSet;
import java.util.Set;

class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length, m = queries.length;
        boolean a = true;
        for (int num : nums) {
            if (num != 0) {
                a = false;
                break;
            }
        }
        if (a) return 0;
        Set<Integer>[] dp = new HashSet[n];
        for (int i = 0; i < n; i++) {
            dp[i] = new HashSet<>();
            dp[i].add(0);
        }
        for (int k = 0; k < m; k++) {
            int l = queries[k][0], r = queries[k][1], v = queries[k][2];
            for (int i = l; i <= r; i++) {
                Set<Integer> newSet = new HashSet<>();
                for (int s : dp[i]) {
                    int ns = s + v;
                    if (ns <= nums[i]) newSet.add(ns);
                }
                dp[i].addAll(newSet);
            }
            boolean ok = true;
            for (int i = 0; i < n; i++) {
                if (!dp[i].contains(nums[i])) {
                    ok = false;
                    break;
                }
            }
            if (ok) return k + 1;
        }
        return -1;
    }
}

