class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        set<int> s;
        for (auto &i : nums) {
            s.insert(i);
        }
        if (s.size() == 1 && *s.begin() == 0) return 0;
        const int MAX_VAL = 1000;
        vector<vector<bool>> dp(nums.size(), vector<bool>(MAX_VAL + 1));
        for (int i = 0; i < (int) nums.size(); i++) {
            dp[i][0] = true;
        }
        for (int q = 0; q < (int) queries.size(); q++) {
            int l = queries[q][0], r = queries[q][1], val = queries[q][2];
            for (int i = l; i <= r; i++) {
                for (int j = MAX_VAL; j >= val; j--) {
                    if (dp[i][j - val]) dp[i][j] = true;
                }
            }
            bool flag = true;
            for (int i = 0; i < (int) nums.size(); i++) {
                if (!dp[i][nums[i]]) {
                    flag = false;
                    break;
                }
            }
            if (flag) return q + 1;
        }
        return -1;
    }
};

