    public static boolean canEat(int[] piles, int h, int k) {

        int hours = 0;

        for (int pile : piles) {
            int div = pile / k;
            hours += div;
            if (pile % k != 0) {
                hours ++;
            }
        }
        return hours <= h;
    }

    public static int abcAsSubString(String s) {

        int[] count = new int[3];
        int ans = 0;
        int left = 0;

        for (int i = 0; i < s.length(); i++) {
            count[s.charAt(i) - 'a'] ++;
            while (count[0] > 0 && count[1] > 0 && count[2] > 0) {
                ans += s.length() - i;
                count[s.charAt(left) - 'a'] --;
                left ++;
            }
        }
        return ans;
