    public static int longestPalindromicSubseq(String s, int i, int j, int[][] dp) {

        if (i > j) {
            return 0;
        }

        if (i == j) {
            return 1;
        }

        if (dp[i][j] != 0) {
            return dp[i][j];
        }

        if (s.charAt(i) == s.charAt(j)) {
            return 2 + longestPalindromicSubseq(s, i + 1, j - 1, dp);
        }

        int a = longestPalindromicSubseq(s, i + 1, j, dp);
        int b = longestPalindromicSubseq(s, i, j - 1, dp);

        return dp[i][j] = Math.max(a, b);
    }
    public static void permutations1(int[] nums, int index, List<Integer> running, List<List<Integer>> ans) {

        // base case
        if (index >= nums.length) {
            ans.add(new ArrayList<>(running));
            return; 
        }

        for (int i = 0; i < nums.length; i++) {
            if (running.contains(nums[i])) continue;
            running.add(nums[i]);
            permutations1(nums, index + 1, running, ans);
            running.remove(running.size() - 1);
        }
    }

    public static int lcs(String s1, String s2, int i, int j, int[][] dp)  {

        if (i == s1.length() || j == s2.length()) {
            return 0;
        }

        if (dp[i][j] != -1) {
            return dp[i][j];
        }

        if (s1.charAt(i) == s2.charAt(j)) {
            return 1 + lcs(s1, s2, i + 1, j + 1, dp);
        }

        int op1 = lcs(s1, s2, i + 1, j, dp);
        int op2 = lcs(s1, s2, i, j + 1, dp);

        return dp[i][j] = Math.max(op1, op2);
    }

    public static int[] dailyTemperatures(int[] temperatures) {


        int n = temperatures.length;
        int[] ans = new int[temperatures.length];
        Stack<Integer> stack = new Stack<>();

        for (int i = n - 1; i >= 0; i--) {
            while (!stack.isEmpty() && temperatures[stack.peek()] <= temperatures[i]) {
                stack.pop();
            }
            if (stack.isEmpty()) {
                ans[i] = 0;
            } else {
                ans[i] = stack.peek() - i;
            }
            stack.add(i);
        }
        return ans;
    }
    public static int[] NGE2(int[] nums) {

        int n = nums.length;
        int[] ans = new int[n];
        int index = n - 1;

        Stack<Integer> stack = new Stack<>();
        for (int i = (2 * n) - 1; i >= 0; i--) {
            while (!stack.isEmpty() && stack.peek() <= nums[i % n]) {
                stack.pop();
            }
            if (i < n) {
                if (stack.isEmpty()) {
                    ans[index--] = -1;
                } else {
                    ans[index--] = stack.peek();
                }
            }

            stack.add(nums[i % n]);
        }
        return ans;
    }
    public static int[] NGE1 (int[] arr) {

        int n = arr.length;
        int index = n - 1;
        int[] ans = new int[n];

        Stack<Integer> stack = new Stack<>(); // []


        for (int i = n - 1; i >= 0; i--) {

            while (!stack.isEmpty() && stack.peek() <= arr[i]) {
                stack.pop();
            }
            if (stack.isEmpty()) {
                ans[index] = -1;
            } else {
                ans[index] = stack.peek();
            }
            stack.add(arr[i]);
            index --;
        }
        return ans;
    }
    public static int maxAreaWater(int[] heights) {

        int s = 0;
        int e = heights.length - 1;

        int nasDaily = 0;

        while (s < e) {
            int ans = Math.min(heights[s], heights[e]);
            int idx = e - s;
            nasDaily = Math.max(nasDaily, ans * idx);
            if (heights[s] < heights[e]) {
                s ++;
            } else {
                e --;
            }
        }
        return nasDaily;
    }
    public static long coloredCell (int n) {

        if (n == 1) {
            return n;
        }

        return (n - 1) * 4 + coloredCell(n - 1);
    }
}

