class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        if (all_of(nums.begin(), nums.end(), [](const int x) { return x == 0; })) {
            return 0;
        }

        vector<bitset<1001>> bitsets(nums.size());
        for (int i = 0; i < bitsets.size(); ++i) {
            bitsets[i][0] = true;
        }

        int count = 0;
        for (const vector<int>& query : queries) {
            count++;
            const int start = query[0];
            const int end = query[1];
            const int val = query[2];

            for (int i = start; i <= end; ++i) {
                bitsets[i] |= (bitsets[i] << val);
            }

            bool ok = true;
            for (int i = 0; i < nums.size(); ++i) {
                if (not bitsets[i][nums[i]]) {
                    ok = false;
                    break;
                } 
            }

            if (ok) {
                return count;
            }
        }
        return -1;
    }
};

