from typing import List

class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        N = len(nums)
        Q = len(queries)
        left = 0
        right = Q
        answer = -1

        def can_reach(target, elements):
            if target == 0:
                return True
            dp = [False] * (target + 1)
            dp[0] = True
            for num in elements:
                for s in range(target, num - 1, -1):
                    if dp[s - num]:
                        dp[s] = True
                        if s == target:
                            return True
            return dp[target]

        def good(target):
            index_vals = [[] for _ in range(N)]
            for q in queries[:target]:
                l, r, v = q
                for i in range(l, r + 1):
                    index_vals[i].append(v)
            for i in range(N):
                required = nums[i]
                if required == 0:
                    continue
                vals = index_vals[i]
                total = sum(vals)
                if total < required:
                    return False
                if not can_reach(required, vals):
                    return False
            return True

        while left <= right:
            mid = (left + right) // 2
            if good(mid):
                answer = mid
                right = mid - 1
            else:
                left = mid + 1
        return answer if answer != -1 else -1

