def subsetSumToK( k, arr):
    if not arr and k==0:
        return True
    if not arr:
        return False
    n = len(arr)
    dp = [[False for j in range(k + 1)] for i in range(n)]
    for i in range(n):
        dp[i][0] = True
    if arr[0] <= k:
        dp[0][arr[0]] = True
    for ind in range(1, n):
        for target in range(1, k + 1):
            notTaken = dp[ind - 1][target] 
            taken = False
            if arr[ind] <= target:
                taken = dp[ind - 1][target - arr[ind]]
            dp[ind][target] = notTaken or taken 
    return dp[n - 1][k]
class Solution:
