            hmm = [[] for _ in range(n)]
            for l,r,val in nq:
                for j in range(l,r+1):
                    hmm[j].append(val)
            return all(subsetSumToK(nums[i],hmm[i]) for i in range(n))
        l = 0
        r = len(queries)
        ans = -1
        while l<=r:
            mid = (l + r)//2
            if check(mid):
                ans = mid
                r = mid - 1
            else:
                l = mid + 1
        return ans

