            if (dp[nums[i]]) continue;
            for (int j = 0; j < queries.size(); j++) {
                if (queries[j][0] > i || queries[j][1] < i) continue;
                for (int k = dp.size() - 1; k >= queries[j][2]; k--) {
                    dp[k] |= dp[k - queries[j][2]];
                }
                if (dp[nums[i]]) {
                    res = max(res, j + 1);
