class Solution {
public:
    int minZeroArray(vector<int>& a, vector<vector<int>>& queries) {
        int l = 0, r = queries.size(), ans = -1;  // Fix binary search range
        int n = a.size();

        while (l <= r) {
            int mid = (l + r) / 2;
            bool ok = true;

            for (int i = 0; i < n; i++) {
                vector<int> values;
                int cnt = 0;

                for (auto& j : queries) {
                    if (cnt >= mid) break;  // Only process 'mid' queries
                    if (j[0] <= i && j[1] >= i) {
                        values.push_back(j[2]);
                    }
                    cnt++;
                }

                vector<int> sums(a[i] + 1, 0);  // Use 0 instead of -1
                sums[0] = 1;

                for (auto val : values) {
                    for (int new_sum = a[i]; new_sum >= 0; new_sum--) {
                        if (sums[new_sum]) {
                            if (new_sum + val <= a[i])
                                sums[new_sum + val] = 1;
                        }
                    }
                }

                if (!sums[a[i]]) ok = false;  // Check if sum is possible
            }

            if (ok) {
                ans = mid;
                r = mid - 1;
            } else {
                l = mid + 1;
            }
        }

        return ans == -1 ? -1 : ans ;
    }
};

