class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:

        @cache
        def recurse(i, j, num):
            if i >= len(queries):
                return float('inf')
            if j not in range(queries[i][0], queries[i][1] + 1) or queries[i][2] > num:
                return recurse(i + 1, j, num)
            elif queries[i][2] == num:
                return i + 1
            else:
                return min(recurse(i + 1, j, num), recurse(i + 1, j, num - queries[i][2]))

        required = 0
        for j, num in enumerate(nums):
            if num == 0: continue
            recurse.cache_clear()
            temp = recurse(0, j, num)
            if temp == float('inf'):
                return -1
            required = max(required, temp)

        return required

