class Solution {
    public boolean all(int[] nums){
        for(int i : nums){
            if(i!=0) return false;
        }
        return true;
    }
    public int minZeroArray(int[] nums, int[][] q) {
        int len = nums.length;
        int low = 0;
        int high = q.length-1;
        int ans =-1;
        if(all(nums)){
            return 0;
        }
        while(low<=high){
            int mid = (low+high)/2;
            if(fkn(nums,q,mid)){
                ans = mid+1;
                high=mid-1;
            }else{
                low=mid+1;
            }
        }
        return ans;
    }
    public boolean fkn(int[] nums,int[][] qu,int mid){
        for(int i =0 ;i<nums.length;i++){
            Queue<Integer> q = new LinkedList<>();
            HashSet<Integer> set = new HashSet<>();
            q.add(0);
            set.add(0);
            boolean flag =false;
            if(nums[i]==0) continue;
            for(int j =0 ;j<=mid;j++){
                if(qu[j][0]<= i && i<= qu[j][1]){
                    int m = qu[j][2];
                    int size = q.size();
                    for(int k =0 ;k <size;k++){
                        int top = q.poll();
                        if(top==nums[i] || top + m ==nums[i] ){
                            flag = true;
                            break;
                        }
                        if(top+m<nums[i] && set.contains(top+m)==false ){
                            set.add(top+m);
                            q.add(top+m);
                        }
                        q.add(top);
                    }
                    if(flag){
                        break;
                    }
                }
            }
            if(flag==false){
                return false;
            }
        }
        return true;
    }
}

