class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)

        def check(k):
            d = [[] for _ in range(n)]
            for i in range(k):
                l, r, v = queries[i]
                for j in range(l, r + 1):
                    d[j].append(v)

            for i, num in enumerate(nums):
                mask = 1
                for v in d[i]:
                    mask |= mask << v
                if mask & (1 << num) == 0:
                    return False
            return True

        l, r = 0, len(queries)
        res = -1

        while l < r:
            m = (l + r) // 2
            if check(m):
                res = m
                r = m
            else:
                l = m + 1
        if res == -1:
            if check(l):
                res = l
        return res

