class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int ans = 0;
        int n = nums.size();
        vector<set<int>> possible(n, set<int>());
        vector<bool> done(n, false);
        bool flag = true;
        for (int i=0; i<n; i++) {
            if (nums[i] == 0) done[i] = true;
            else flag = false;
        }
        if (flag) return 0;
        for (auto &query: queries) {
            int l = query[0];
            int r = query[1];
            int val = query[2];

            for (int i=l; i<=r; i++) {
                if (done[i]) continue;
                vector<int> newpos = {val};
                for (auto it=possible[i].begin(); it!=possible[i].end(); it++) {
                    newpos.push_back(val + (*it));
                }
                for (auto el: newpos) possible[i].insert(el);
            }
            ans++;
            bool flag = true;
            for (int i=0; i<n; i++) {
                if (done[i]) continue;
                if (possible[i].find(nums[i]) != possible[i].end()) {
                    done[i] = true;
                } else flag = false;
            }
            if (flag) return ans;
        }
        return -1;
    }
};

