        vis = [False for _ in nums]
        zero = 0
        for i,n in enumerate(nums):
            if n==0:
                vis[i] = True
                zero+=1
            num_set[i].add(n)
        counter = 0
        if zero==len(nums):
            return 0
        for q in queries:
            for j in range(q[0],q[1]+1):
                if vis[j]:
                    continue

                if q[2] in num_set[j]:
