class Solution {
public:
    bool f(vector<int>& s, int i, int t, vector<vector<int>>& memo) {
        if (t == 0) {
            return true;
        }
        if (i >= s.size()) {
            return false;
        }
        if (memo[i][t] != -1) {
            return memo[i][t];
        }
        bool a = f(s, i + 1, t, memo);
        bool b = false;
        if (s[i] <= t) {
            b = f(s, i + 1, t - s[i], memo);
        }
        memo[i][t] = a || b;
        return memo[i][t];
    }

    bool possible(vector<int>& nums, vector<vector<int>>& queries, int k) {
        int n = nums.size();
        for (int j = 0; j < n; j++) {
            int t = nums[j];
            vector<int> s;
            for (int i = 0; i < k; i++) {
                if (queries[i][0] <= j && j <= queries[i][1]) {
                    s.push_back(queries[i][2]);
                }
            }
            if (t == 0) {
                continue;
            }
            int z = s.size();
            vector<vector<int>> memo(z, vector<int>(t + 1, -1));
            if (!f(s, 0, t, memo)) {
                return false;
            }
        }
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int M = queries.size();
        bool allZero = true;
        for (int x : nums) {
            if (x != 0) {
                allZero = false;
                break;
            }
        }
        if (allZero) {
            return 0;
        }

        int lo = 1, hi = M, ans = -1;
        while (lo <= hi) {
            int mid = lo + (hi - lo) / 2;
            if (possible(nums, queries, mid)) {
                ans = mid;
                hi = mid - 1;
            } else {
                lo = mid + 1;
            }
        }
        return ans;
    }
};

