        for (int i = 0; i < n; i++) { f[i].resize(nums[i] + 1); f[i][0] = 1;}
        int res = 0;
        auto check = [&]() {
            for (int i = 0; i < n;i ++) {
                if(!f[i][nums[i]]) return false;
            }
            return true;
        };
        if (check()) return res;

        for (auto& q:  qs) {
            int l = q[0], r =  q[1], val = q[2];
            for (int i = l; i <= r; i++) {
                for (int j = nums[i]; j >= val; j--) {
                    f[i][j]  |= f[i][j - val];
                }
            }
            res ++;
            if (check()) return res;

        }
        return -1;
    }
};

