class Solution:

    def coinChange(self, currInd, currVal, currentlyProcessing):

        if currInd == len(self.queries) and currVal == 0:
            return -1
        elif currInd == len(self.queries):
            return len(self.queries)
        elif currVal < 0:
            return len(self.queries)

        if (currInd, currVal) in self.memo:
            return self.memo[(currInd, currVal)]

        if self.queries[currInd][0] <= currentlyProcessing and currentlyProcessing <= self.queries[currInd][1]:
            self.memo[(currInd, currVal)] = min(
                self.coinChange(currInd + 1, currVal, currentlyProcessing),
                max(currInd, self.coinChange(currInd + 1, currVal - self.queries[currInd][2], currentlyProcessing))
            )
        else:
            self.memo[(currInd, currVal)] = self.coinChange(currInd + 1, currVal, currentlyProcessing)

        return self.memo[(currInd, currVal)]

    def minZeroArray(self, nums, queries):
        self.queries = queries
        maxInd = -1
        for i in range(len(nums)):
            self.memo = dict()
            maxInd = max(self.coinChange(0, nums[i], i), maxInd)
        return maxInd + 1 if maxInd != len(self.queries) else -1

