class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size();
        int ans = -1;

        auto get_res = [&] (int idx) {
            vector<vector<int>> dp(m, vector<int>(nums[idx]+1, -1));
            auto dfs = [&] (this auto&& dfs, int i, int j) {
                if(j == 0) {
                    return i;
                }
                if(i == m) {
                    return m + 1;
                }
                if(dp[i][j] != -1) return dp[i][j];
                int l = queries[i][0], r = queries[i][1], val = queries[i][2];
                int res = dfs(i+1, j);
                if(l <= idx && idx <= r && j >= val) {
                    res = min(res, dfs(i+1, j - val));
                }
                dp[i][j] = res;
                return res;
            };
            return dfs(0, nums[idx]);
        };
        for(int i=0; i<n; i++) {
            int res = get_res(i);
            // cout << res << ' ';
            ans = max(ans, res);
        }

        return ans == m+1 ? -1 : ans;
    }
};

