            d = [[] for i in range(n)]
            for i, j, v in queries[:k]:
                for idx in range(i, j + 1):
                    d[idx].append(v)
            for idx in range(n):
                target = nums[idx]
                if target == 0:
                    continue
                a = d[idx]
                s = {0}
                valid = False
                for x in a:
                    if x > target:
                        continue
                    new_s = {0}
                    for y in s:
                        if x + y > target:
                            continue
                        if x + y == target:
                            valid = True
                            break
                        new_s.add(x + y)
                    if valid:
                        break
                    for v in new_s:
                        s.add(v)
                if not valid:
                    return False
            return True

        l, r = 0, len(queries) + 1
        while l < r:
            m = (l + r) // 2
            if valid(m):
                r = m
            else:
                l = m + 1
        return l if l <= len(queries) else -1

