class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size(), m = queries.size();

        auto possible = [&] (int k) -> bool {
            if (k == 0) {
                return *max_element(nums.begin(), nums.end()) == 0;
            }

            vector<vector<int>> vals(n, vector<int> (1));
            for (int i = 0; i < k; i++) {
                int l = queries[i][0], r = queries[i][1], val = queries[i][2];
                for (int j = l; j <= r; j++) {
                    vals[j].push_back(val);
                }
            }

            for (int idx = 0; idx < n; idx++) {
                int sz = vals[idx].size();
                vector<vector<bool>> dp(sz + 1, vector<bool> (nums[idx] + 1));
                dp[sz][0] = true;
                for (int i = sz - 1; i >= 0; i--) {
                    for (int j = 0; j <= nums[idx]; j++) {
                        dp[i][j] = dp[i + 1][j];
                        if (vals[idx][i] <= j) {
                            dp[i][j] = (dp[i][j] | dp[i + 1][j - vals[idx][i]]);
                        }
                    }
                }

                if (!dp[0][nums[idx]]) return false;
            }

            return true;
        };

        int low = 0, high = m;
        while (low <= high) {
            int mid = low + (high - low) / 2;
            if (possible(mid)) {
                high = mid - 1;
            } else {
                low = mid + 1;
            }
        }

        return high + 1 <= m ? high + 1 : -1;
    }
};

