class Solution {
private:
    bool isPos(vector<int>& v, int k) {
        int n = v.size();
        vector<vector<bool>> dp(n + 1, vector<bool>(k + 1, false));

        for (int i = 0; i <= n; i++)
            dp[i][0] = true;

        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= k; j++) {
                if (v[i - 1] <= j)
                    dp[i][j] = dp[i - 1][j] || dp[i - 1][j - v[i - 1]];
                else
                    dp[i][j] = dp[i - 1][j];
            }
        }
        return dp[n][k];
    }

    bool check(vector<int>& a, vector<vector<int>>& q, int k) {
        int n = a.size();
        vector<vector<int>> v(n);

        for (int i = 0; i < k; i++) {
            for (int j = q[i][0]; j <= q[i][1]; j++) {
                v[j].push_back(q[i][2]);
            }
        }

        for (int i = 0; i < n; i++) {
            if (!isPos(v[i], a[i]))
                return false;
        }
        return true;
    }

public:
    int minZeroArray(vector<int>& a, vector<vector<int>>& q) {
        int n = a.size(), m = q.size();
        int l = -1, r = m;

        while (l + 1 < r) {
            int mid = (l + r) / 2;

            if (check(a, q, mid)) {
                r = mid;
            } else
                l = mid;
        }
        if(!check(a, q, r))
            r = -1;

        return r;
    }
};

